import React from 'react';

function PhotosTab({ 
  photoUrl, setPhotoUrl, photoCaption, setPhotoCaption,
  photos, setPhotos, addPhoto
}) {
  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
      <div>
        <h3 className="font-semibold mb-3">Add Photos</h3>

        <div className="space-y-3 bg-white border border-gray-200 p-4 rounded-lg">
          <div>
            <label className="block text-gray-700 mb-1 text-sm">Image URL</label>
            <input
              type="text"
              value={photoUrl}
              onChange={e => setPhotoUrl(e.target.value)}
              placeholder="https://example.com/image.jpg"
              className="w-full p-2 border border-gray-300 rounded"
            />
            <p className="text-xs text-gray-500 mt-1">Enter the URL of an image you found online</p>
          </div>

          <div>
            <label className="block text-gray-700 mb-1 text-sm">Caption</label>
            <input
              type="text"
              value={photoCaption}
              onChange={e => setPhotoCaption(e.target.value)}
              placeholder="Place to visit, idea, etc."
              className="w-full p-2 border border-gray-300 rounded"
            />
          </div>

          <button
            onClick={addPhoto}
            disabled={!photoUrl}
            className={`w-full py-2 rounded ${
              !photoUrl
                ? 'bg-gray-300 cursor-not-allowed' 
                : 'bg-blue-500 text-white hover:bg-blue-600'
            }`}
          >
            Add Photo
          </button>
        </div>

        <div className="mt-4 bg-yellow-50 border border-yellow-200 p-3 rounded text-sm">
          <h4 className="font-semibold text-yellow-800">Photo Gallery Tips:</h4>
          <ul className="mt-1 ml-4 list-disc text-yellow-800">
            <li>Save photos of places you want to visit</li>
            <li>Collect inspirational travel images</li>
            <li>Create a visual wishlist for your trip</li>
          </ul>
        </div>
      </div>

      <div>
        <h3 className="font-semibold mb-3">Your Photos</h3>

        {photos.length === 0 ? (
          <div className="bg-gray-50 p-4 rounded-lg text-center">
            <p className="text-gray-500">No photos added yet.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
            {photos.map(photo => (
              <div key={photo.id} className="bg-white border border-gray-200 rounded-lg overflow-hidden">
                <div className="relative">
                  <img 
                    src={photo.url} 
                    alt={photo.caption || "Travel inspiration"}
                    className="w-full h-36 object-cover"
                    onError={e => {
                      e.target.onerror = null;
                      e.target.src = "/api/placeholder/300/200";
                    }}
                  />
                  <button
                    onClick={() => setPhotos(photos.filter(p => p.id !== photo.id))}
                    className="absolute top-2 right-2 bg-red-500 text-white w-6 h-6 rounded-full flex items-center justify-center hover:bg-red-600"
                  >
                    âœ•
                  </button>
                </div>
                {photo.caption && (
                  <div className="p-2 text-sm">
                    {photo.caption}
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default PhotosTab;
