// src/components/Subscription/PremiumContentGuard.jsx

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSubscription } from '../../hooks/useSubscription';

/**
 * Component that guards premium content
 * Only allows access to children if user has an active subscription
 */
const PremiumContentGuard = ({ children, showPreview = false, previewContent = null }) => {
  const { isSubscribed, isLoading } = useSubscription();
  const navigate = useNavigate();
  const [isShowingDetails, setIsShowingDetails] = useState(false);
  
  // Show loading state while checking subscription status
  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="animate-spin rounded-full h-10 w-10 border-b-2 border-blue-600"></div>
        <p className="ml-2">Checking subscription status...</p>
      </div>
    );
  }
  
  // If subscribed, render children
  if (isSubscribed) {
    return children;
  }
  
  // Otherwise, show subscription gate
  return (
    <div className="max-w-lg mx-auto p-6 bg-gray-50 rounded-lg mt-8 border border-gray-200">
      {/* Preview content (if enabled) */}
      {showPreview && previewContent && (
        <div className="mb-6">
          {previewContent}
        </div>
      )}
      
      {/* Premium content gate */}
      <div className="text-center">
        <div className="mb-4">
          <svg className="w-16 h-16 text-blue-600 mx-auto" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z"></path>
          </svg>
        </div>
        
        <h2 className="text-2xl font-bold text-gray-800 mb-2">
          Premium Feature
        </h2>
        
        <p className="text-gray-600 mb-4">
          This feature requires a premium subscription.
          {!isShowingDetails && (
            <button
              onClick={() => setIsShowingDetails(true)}
              className="ml-1 text-blue-600 hover:underline"
            >
              View details
            </button>
          )}
        </p>
        
        {isShowingDetails && (
          <div className="bg-white p-4 rounded-md mb-6 border border-gray-200">
            <h3 className="font-semibold text-gray-800 mb-2">Premium Benefits:</h3>
            <ul className="text-left list-disc pl-8 mb-4 text-gray-700">
              <li>Unlimited flight deal alerts</li>
              <li>Real-time price drop notifications</li>
              <li>Advanced trip planning tools</li>
              <li>Personalized travel recommendations</li>
              <li>Ad-free experience</li>
            </ul>
            <p className="text-sm text-gray-600">
              Starting at just $4.99 per month. Cancel anytime.
            </p>
          </div>
        )}
        
        <div className="flex flex-col sm:flex-row justify-center gap-4">
          <button
            onClick={() => navigate('/subscription/plans')}
            className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
          >
            Subscribe Now
          </button>
          
          <button
            onClick={() => navigate('/')}
            className="border border-gray-300 bg-white text-gray-700 px-6 py-2 rounded hover:bg-gray-50"
          >
            Return to Dashboard
          </button>
        </div>
      </div>
    </div>
  );
};

export default PremiumContentGuard;
