import React, { useState } from 'react';
import { calculateDuration, shareTrip, exportTripToPDF, emailTripDetails } from '../../utils/helpers';
import ExpenseTracker from '../ExpenseTracker/ExpenseTracker';
import BudgetChart from '../Budget/BudgetChart';
import DynamicCalendar from '../Calendar/DynamicCalendar';
import ItineraryView from '../Itinerary/ItineraryView';
import { useI18n } from '../../utils/i18n'; // Import the i18n hook

function TripDetails({ trip, editTrip, closeTrip, shareEmail, setShareEmail }) {
  const [activeTab, setActiveTab] = useState('overview');
  const [tripExpenses, setTripExpenses] = useState(trip.expenses || []);
  
  // Get i18n functionality
  const { t } = useI18n(); // Use the i18n hook
  
  // Update trip expenses (in a real app, this would update the trip in a database)
  const updateTripExpenses = (expenses) => {
    setTripExpenses(expenses);
    // In a full implementation, this would update the parent trip state as well
  };

  // Simple function to safely use the translation function
  const safeTranslate = (key, defaultText) => {
    // Check if t is a function before using it
    if (typeof t === 'function') {
      return t(key, defaultText);
    }
    // Fallback to the default text if t is not a function
    return defaultText;
  };

  return (
    <div className="max-w-6xl mx-auto">
      <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
        <div className="flex justify-between items-start mb-6">
          <div>
            <h2 className="text-3xl font-bold text-gray-800">{trip.destination}</h2>
            <p className="text-gray-600">
              {trip.startDate} {safeTranslate('trips.to', 'to')} {trip.endDate} ({calculateDuration(trip.startDate, trip.endDate)} {safeTranslate('trips.days', 'days')})
            </p>
          </div>
          <div className="flex gap-2">
            <button
              onClick={() => editTrip(trip)}
              className="bg-yellow-500 text-white px-3 py-1 rounded hover:bg-yellow-600"
            >
              {safeTranslate('trips.editTrip', 'Edit Trip')}
            </button>
            <button
              onClick={closeTrip}
              className="bg-gray-500 text-white px-3 py-1 rounded hover:bg-gray-600"
            >
              {safeTranslate('tripDetails.backToTrips', 'Back to Trips')}
            </button>
          </div>
        </div>
        
        {/* Tab Navigation */}
        <div className="border-b border-gray-200 mb-6">
          <div className="flex flex-wrap -mb-px">
            <button
              className={`mr-2 py-2 px-4 font-medium ${
                activeTab === 'overview' 
                  ? 'border-b-2 border-blue-500 text-blue-600' 
                  : 'text-gray-500 hover:text-gray-700'
              }`}
              onClick={() => setActiveTab('overview')}
            >
              {safeTranslate('tripDetails.overview', 'Overview')}
            </button>
            <button
              className={`mr-2 py-2 px-4 font-medium ${
                activeTab === 'itinerary' 
                  ? 'border-b-2 border-blue-500 text-blue-600' 
                  : 'text-gray-500 hover:text-gray-700'
              }`}
              onClick={() => setActiveTab('itinerary')}
            >
              {safeTranslate('tripDetails.itinerary', 'Itinerary')}
            </button>
            <button
              className={`mr-2 py-2 px-4 font-medium ${
                activeTab === 'tasks' 
                  ? 'border-b-2 border-blue-500 text-blue-600' 
                  : 'text-gray-500 hover:text-gray-700'
              }`}
              onClick={() => setActiveTab('tasks')}
            >
              {safeTranslate('tripDetails.tasks', 'Tasks')}
            </button>
            <button
              className={`mr-2 py-2 px-4 font-medium ${
                activeTab === 'expenses' 
                  ? 'border-b-2 border-blue-500 text-blue-600' 
                  : 'text-gray-500 hover:text-gray-700'
              }`}
              onClick={() => setActiveTab('expenses')}
            >
              {safeTranslate('tripDetails.expenses', 'Expenses')}
            </button>
            <button
              className={`mr-2 py-2 px-4 font-medium ${
                activeTab === 'share' 
                  ? 'border-b-2 border-blue-500 text-blue-600' 
                  : 'text-gray-500 hover:text-gray-700'
              }`}
              onClick={() => setActiveTab('share')}
            >
              {safeTranslate('tripDetails.share', 'Share')}
            </button>
          </div>
        </div>
        
        {/* Overview Tab */}
        {activeTab === 'overview' && (
          <div>
            {/* Trip Budget */}
            <div className="mb-8">
              <h3 className="text-xl font-semibold mb-4 text-gray-800">{safeTranslate('tripDetails.budget', 'Budget')}</h3>
              <div className="bg-blue-50 p-4 rounded-lg">
                <div className="flex flex-col md:flex-row gap-6">
                  <div className="flex-1">
                    <h4 className="font-semibold mb-2">{safeTranslate('budget.totalBudget', 'Total Budget')}</h4>
                    <p className="text-2xl font-bold text-blue-700">${trip.budget}</p>
                    {trip.budgetBreakdown && (
                      <div className="mt-3 space-y-1">
                        <p><span className="font-medium">{safeTranslate('budget.accommodation', 'Accommodation')}:</span> ${trip.budgetBreakdown.accommodation}</p>
                        <p><span className="font-medium">{safeTranslate('budget.food', 'Food')}:</span> ${trip.budgetBreakdown.food}</p>
                        <p><span className="font-medium">{safeTranslate('budget.transportation', 'Transportation')}:</span> ${trip.budgetBreakdown.transportation}</p>
                        <p><span className="font-medium">{safeTranslate('budget.activities', 'Activities')}:</span> ${trip.budgetBreakdown.activities}</p>
                        <p><span className="font-medium">{safeTranslate('budget.other', 'Other')}:</span> ${trip.budgetBreakdown.other}</p>
                      </div>
                    )}
                  </div>
                  
                  <div className="flex-1">
                    <h4 className="font-semibold mb-2">{safeTranslate('tripDetails.budgetVisualization', 'Budget Visualization')}</h4>
                    <BudgetChart budgetData={trip.budgetBreakdown || {}} />
                  </div>
                </div>
              </div>
            </div>
            
            {/* Trip Details Sections */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
              {/* Transportation Section */}
              <div>
                <h3 className="text-xl font-semibold mb-4 text-gray-800">{safeTranslate('tripDetails.transportation', 'Transportation')}</h3>
                {trip.transports && trip.transports.length > 0 ? (
                  <div className="bg-blue-50 p-4 rounded-lg">
                    <div className="space-y-3">
                      {trip.transports.map(t => (
                        <div key={t.id} className="bg-white p-3 rounded-lg shadow-sm">
                          <div className="font-medium">{t.type}</div>
                          <div className="text-sm">{safeTranslate('transport.from', 'From')}: {t.from}</div>
                          <div className="text-sm">{safeTranslate('transport.to', 'To')}: {t.to}</div>
                          {t.price && <div className="text-sm">{safeTranslate('transport.price', 'Price')}: ${t.price}</div>}
                        </div>
                      ))}
                    </div>
                  </div>
                ) : (
                  <div className="bg-gray-50 p-4 rounded-lg text-center">
                    <p className="text-gray-500">{safeTranslate('tripDetails.noTransportation', 'No transportation options added.')}</p>
                  </div>
                )}
              </div>
              
              {/* Tasks Section (replacing Local Information) */}
              <div>
                <h3 className="text-xl font-semibold mb-4 text-gray-800">{safeTranslate('tripDetails.tripTasks', 'Trip Tasks')}</h3>
                {trip.tasks && trip.tasks.length > 0 ? (
                  <div className="bg-blue-50 p-4 rounded-lg">
                    <div className="space-y-2">
                      {trip.tasks.slice(0, 5).map(task => (
                        <div key={task.id} className="bg-white p-3 rounded-lg shadow-sm flex items-center">
                          <div className="mr-3">
                            {task.completed ? 
                              <span className="text-green-500">✓</span> : 
                              <span className="text-yellow-500">○</span>}
                          </div>
                          <div className="flex-1">
                            <div className={`font-medium ${task.completed ? 'line-through text-gray-500' : ''}`}>
                              {task.text}
                            </div>
                            {task.date && <div className="text-xs text-gray-500">{safeTranslate('tasks.dueDate', 'Due')}: {task.date}</div>}
                          </div>
                        </div>
                      ))}
                      
                      {trip.tasks.length > 5 && (
                        <div className="text-center mt-2">
                          <button 
                            onClick={() => setActiveTab('tasks')}
                            className="text-blue-500 text-sm"
                          >
                            {safeTranslate('tripDetails.viewAllTasks', 'View all')} {trip.tasks.length} {safeTranslate('tripDetails.tasks', 'tasks')}
                          </button>
                        </div>
                      )}
                    </div>
                  </div>
                ) : (
                  <div className="bg-gray-50 p-4 rounded-lg text-center">
                    <p className="text-gray-500">{safeTranslate('tripDetails.noTasks', 'No tasks added to this trip.')}</p>
                  </div>
                )}
              </div>
            </div>
            
            {/* Photos Gallery */}
            <div className="mb-8">
              <h3 className="text-xl font-semibold mb-4 text-gray-800">{safeTranslate('tripDetails.photoGallery', 'Photo Gallery')}</h3>
              {trip.photos && trip.photos.length > 0 ? (
                <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
                  {trip.photos.map(photo => (
                    <div key={photo.id} className="bg-white border border-gray-200 rounded-lg overflow-hidden shadow-sm">
                      <img 
                        src={photo.url} 
                        alt={photo.caption || safeTranslate('photos.travelInspiration', 'Travel inspiration')}
                        className="w-full h-36 object-cover"
                        onError={e => {
                          e.target.onerror = null;
                          e.target.src = "/api/placeholder/300/200";
                        }}
                      />
                      {photo.caption && (
                        <div className="p-2 text-sm">
                          {photo.caption}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              ) : (
                <div className="bg-gray-50 p-4 rounded-lg text-center">
                  <p className="text-gray-500">{safeTranslate('tripDetails.noPhotos', 'No photos added to this trip.')}</p>
                </div>
              )}
            </div>
            
            {/* Notes Section */}
            <div className="mb-8">
              <h3 className="text-xl font-semibold mb-4 text-gray-800">{safeTranslate('tripDetails.tripNotes', 'Trip Notes')}</h3>
              {trip.notes && trip.notes.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {trip.notes.map(note => (
                    <div key={note.id} className="bg-blue-50 p-3 rounded-lg">
                      <div className="flex items-center mb-1">
                        <div className="font-medium">{note.title}</div>
                        {note.category && (
                          <span className="ml-2 px-2 py-0.5 bg-blue-100 text-blue-800 text-xs rounded-full">
                            {note.category}
                          </span>
                        )}
                      </div>
                      <div className="text-sm mt-1">{note.text}</div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="bg-gray-50 p-4 rounded-lg text-center">
                  <p className="text-gray-500">{safeTranslate('tripDetails.noNotes', 'No notes added to this trip.')}</p>
                </div>
              )}
            </div>
            
            {/* External Services */}
            <div>
              <h3 className="text-xl font-semibold mb-4 text-gray-800">{safeTranslate('tripDetails.externalServices', 'External Services')}</h3>
              {trip.externalServices && trip.externalServices.length > 0 ? (
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
                  {trip.externalServices.map(service => (
                    <div key={service.id} className="bg-blue-50 p-3 rounded-lg">
                      <div className="font-medium flex items-center">
                        {service.type === 'Map' && <span className="mr-2">🗺️</span>}
                        {service.type === 'Booking' && <span className="mr-2">🏨</span>}
                        {service.type === 'Weather' && <span className="mr-2">🌤️</span>}
                        {service.type === 'Translation' && <span className="mr-2">🔤</span>}
                        {service.type === 'Advisory' && <span className="mr-2">⚠️</span>}
                        {service.type === 'Other' && <span className="mr-2">🔗</span>}
                        {service.type}
                      </div>
                      
                      <div className="text-sm mt-1">
                        <a 
                          href={service.url.startsWith('http') ? service.url : `https://${service.url}`} 
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-blue-600 hover:underline break-words"
                        >
                          {service.url}
                        </a>
                      </div>
                      
                      {service.note && (
                        <div className="text-sm mt-1 text-gray-600">
                          {service.note}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              ) : (
                <div className="bg-gray-50 p-4 rounded-lg text-center">
                  <p className="text-gray-500">{safeTranslate('tripDetails.noExternalServices', 'No external services added to this trip.')}</p>
                </div>
              )}
            </div>
          </div>
        )}
        
        {/* Itinerary Tab */}
        {activeTab === 'itinerary' && (
          <div>
            <h3 className="text-xl font-semibold mb-4 text-gray-800">
              {safeTranslate('itinerary.title', 'Trip Itinerary')}
            </h3>
            {trip.tasks && trip.tasks.length > 0 ? (
              <div className="bg-blue-50 p-4 rounded-lg">
                <div className="space-y-4">
                  {/* Group tasks by date */}
                  {Object.entries(
                    trip.tasks
                      .filter(task => task.date)
                      .reduce((days, task) => {
                        if (!days[task.date]) days[task.date] = [];
                        days[task.date].push(task);
                        return days;
                      }, {})
                  )
                    .sort(([dateA], [dateB]) => new Date(dateA) - new Date(dateB))
                    .map(([date, tasks]) => (
                      <div key={date} className="bg-white p-3 rounded-lg shadow-sm">
                        <h4 className="font-medium">{date}</h4>
                        <div className="mt-2 space-y-2">
                          {tasks
                            .sort((a, b) => {
                              if (a.time && b.time) return a.time.localeCompare(b.time);
                              if (a.time) return -1;
                              if (b.time) return 1;
                              return 0;
                            })
                            .map(task => (
                              <div key={task.id} className="flex items-start">
                                {task.time && (
                                  <div className="text-sm font-medium mr-2 text-gray-700">
                                    {task.time.substring(0, 5)}
                                  </div>
                                )}
                                <div className={`flex items-start flex-1 ${task.completed ? 'text-gray-500' : ''}`}>
                                  <span className={`mt-0.5 mr-2 ${task.completed ? 'text-green-500' : 'text-gray-400'}`}>
                                    {task.completed ? '✓' : '○'}
                                  </span>
                                  <div className={task.completed ? 'line-through' : ''}>
                                    <p>{task.text}</p>
                                    <p className="text-xs text-gray-500 capitalize">{task.category}</p>
                                  </div>
                                </div>
                              </div>
                            ))}
                        </div>
                      </div>
                    ))}
                </div>
              </div>
            ) : (
              <div className="bg-gray-50 p-4 rounded-lg text-center">
                <p className="text-gray-500">{safeTranslate('itinerary.noEvents', 'No events scheduled for this trip.')}</p>
              </div>
            )}
          </div>
        )}
        
        {/* Tasks Tab */}
        {activeTab === 'tasks' && (
          <div>
            <h3 className="text-xl font-semibold mb-4 text-gray-800">{safeTranslate('tripDetails.tripTasks', 'Trip Tasks')}</h3>
            
            {(!trip.tasks || trip.tasks.length === 0) ? (
              <div className="bg-gray-50 p-4 rounded-lg text-center">
                <p className="text-gray-500">{safeTranslate('tripDetails.noTasksAdded', 'No tasks have been added to this trip.')}</p>
                <button
                  onClick={() => editTrip(trip)}
                  className="mt-3 bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
                >
                  {safeTranslate('tripDetails.addTasks', 'Add Tasks')}
                </button>
              </div>
            ) : (
              <div>
                {/* Task Progress */}
                <div className="bg-blue-50 p-4 rounded-lg mb-6">
                  <div className="flex justify-between items-center">
                    <h4 className="font-semibold">{safeTranslate('tripDetails.overallProgress', 'Overall Progress')}</h4>
                    <div className="text-right">
                      <span className="font-medium">
                        {trip.tasks.filter(task => task.completed).length} {safeTranslate('tasks.of', 'of')} {trip.tasks.length} {safeTranslate('tripDetails.tasksComplete', 'tasks complete')}
                      </span>
                      <span className="ml-2 px-2 py-1 bg-blue-100 rounded-full text-sm">
                        {Math.round((trip.tasks.filter(task => task.completed).length / trip.tasks.length) * 100)}%
                      </span>
                    </div>
                  </div>
                  
                  <div className="mt-3 w-full bg-gray-200 rounded-full h-3">
                    <div
                      className="bg-blue-600 h-3 rounded-full"
                      style={{ width: `${(trip.tasks.filter(task => task.completed).length / trip.tasks.length) * 100}%` }}
                    ></div>
                  </div>
                </div>
                
                {/* Tasks Grouped by Category */}
                <div className="space-y-6">
                  {/* Organize tasks by category */}
                  {Object.entries(
                    trip.tasks.reduce((acc, task) => {
                      const category = task.category || 'other';
                      if (!acc[category]) {
                        acc[category] = [];
                      }
                      acc[category].push(task);
                      return acc;
                    }, {})
                  ).map(([category, categoryTasks]) => (
                    <div key={category} className="bg-white border border-gray-200 rounded-lg overflow-hidden">
                      <div className="bg-gray-100 px-4 py-2 font-medium capitalize">
                        {category}
                        <span className="ml-2 text-sm text-gray-600">
                          ({categoryTasks.filter(t => t.completed).length}/{categoryTasks.length} {safeTranslate('tasks.completed', 'completed')})
                        </span>
                      </div>
                      <div>
                        {categoryTasks.map(task => (
                          <div 
                            key={task.id} 
                            className={`flex items-start p-3 border-b border-gray-100 last:border-b-0 ${task.completed ? 'bg-gray-50' : ''}`}
                          >
                            <div className="flex-1">
                              <p className={`${task.completed ? 'line-through text-gray-500' : ''}`}>
                                {task.text}
                              </p>
                              <div className="mt-1 flex text-xs space-x-2">
                                {task.date && (
                                  <span className="px-2 py-0.5 rounded-full bg-green-100 text-green-800">
                                    {new Date(task.date).toLocaleDateString()}
                                  </span>
                                )}
                                <span className={`px-2 py-0.5 rounded-full bg-gray-100 ${
                                  task.priority === 'high' 
                                    ? 'text-red-600' 
                                    : task.priority === 'medium'
                                      ? 'text-yellow-600'
                                      : 'text-green-600'
                                }`}>
                                  {task.priority 
                                    ? safeTranslate(`tasks.priorities.${task.priority}`, task.priority.charAt(0).toUpperCase() + task.priority.slice(1)) 
                                    : safeTranslate('tasks.priorities.medium', 'Medium')
                                  } {safeTranslate('tripDetails.priority', 'Priority')}
                                </span>
                              </div>
                            </div>
                            <div className="ml-2 flex items-center">
                              {task.completed ? (
                                <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">
                                  {safeTranslate('tripDetails.completed', 'Completed')}
                                </span>
                              ) : (
                                <span className="px-2 py-1 bg-yellow-100 text-yellow-800 text-xs rounded-full">
                                  {safeTranslate('tripDetails.pending', 'Pending')}
                                </span>
                              )}
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
                
                <div className="mt-6 text-center">
                  <button
                    onClick={() => editTrip(trip)}
                    className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                  >
                    {safeTranslate('tripDetails.manageTasks', 'Manage Tasks')}
                  </button>
                </div>
              </div>
            )}
          </div>
        )}
        
        {/* Expenses Tab */}
        {activeTab === 'expenses' && (
          <ExpenseTracker 
            tripExpenses={tripExpenses}
            updateTripExpenses={updateTripExpenses}
          />
        )}
        
        {/* Share Tab */}
        {activeTab === 'share' && (
          <div>
            <h3 className="text-xl font-semibold mb-4 text-gray-800">{safeTranslate('tripDetails.shareYourTrip', 'Share Your Trip')}</h3>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <h4 className="font-semibold mb-3">{safeTranslate('tripDetails.shareViaSocial', 'Share via Social Media')}</h4>
                <div className="bg-white border border-gray-200 p-4 rounded-lg space-y-4">
                  <button
                    onClick={() => shareTrip(trip, 'Facebook')}
                    className="w-full flex items-center justify-center bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
                  >
                    <span className="mr-2">Facebook</span>
                  </button>
                  <button
                    onClick={() => shareTrip(trip, 'Twitter')}
                    className="w-full flex items-center justify-center bg-blue-400 text-white px-4 py-2 rounded-lg hover:bg-blue-500"
                  >
                    <span className="mr-2">Twitter</span>
                  </button>
                  <button
                    onClick={() => shareTrip(trip, 'WhatsApp')}
                    className="w-full flex items-center justify-center bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600"
                  >
                    <span className="mr-2">WhatsApp</span>
                  </button>
                </div>
              </div>
              
              <div>
                <h4 className="font-semibold mb-3">{safeTranslate('tripDetails.exportOptions', 'Export Options')}</h4>
                <div className="bg-white border border-gray-200 p-4 rounded-lg space-y-4">
                  <button
                    onClick={() => exportTripToPDF(trip)}
                    className="w-full flex items-center justify-center bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600"
                  >
                    <span className="mr-2">{safeTranslate('tripDetails.exportAsPDF', 'Export as PDF')}</span>
                  </button>
                  
                  <div className="pt-2">
                    <label className="block text-gray-700 mb-2">{safeTranslate('tripDetails.emailItinerary', 'Email Itinerary')}</label>
                    <div className="flex gap-2">
                      <input
                        type="email"
                        value={shareEmail}
                        onChange={e => setShareEmail(e.target.value)}
                        placeholder={safeTranslate('tripDetails.enterEmail', 'Enter email address')}
                        className="flex-1 p-2 border border-gray-300 rounded"
                      />
                      <button
                        onClick={() => emailTripDetails(trip, shareEmail)}
                        disabled={!shareEmail}
                        className={`px-4 py-2 rounded ${
                          !shareEmail
                            ? 'bg-gray-300 cursor-not-allowed'
                            : 'bg-blue-500 text-white hover:bg-blue-600'
                        }`}
                      >
                        {safeTranslate('tripDetails.send', 'Send')}
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            
            {/* Collaborate Section */}
            <div className="mt-6">
              <h4 className="font-semibold mb-3">{safeTranslate('tripDetails.collaborateWithCompanions', 'Collaborate with Travel Companions')}</h4>
              <div className="bg-white border border-gray-200 p-4 rounded-lg">
                <p className="text-gray-600 mb-3">
                  {safeTranslate('tripDetails.inviteToContribute', 'Invite others to view and contribute to your trip planning.')}
                </p>
                
                <div className="space-y-3">
                  <div className="flex gap-2">
                    <input
                      type="email"
                      placeholder={safeTranslate('tripDetails.enterCompanionEmail', "Enter companion's email")}
                      className="flex-1 p-2 border border-gray-300 rounded"
                    />
                    <select className="p-2 border border-gray-300 rounded">
                      <option value="view">{safeTranslate('tripDetails.canView', 'Can View')}</option>
                      <option value="edit">{safeTranslate('tripDetails.canEdit', 'Can Edit')}</option>
                    </select>
                    <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
                      {safeTranslate('tripDetails.invite', 'Invite')}
                    </button>
                  </div>
                  
                  <div className="bg-blue-50 p-3 rounded-lg">
                    <h5 className="font-medium mb-2">{safeTranslate('tripDetails.sharedWith', 'Shared With')}</h5>
                    {/* In a real app, this would show actual collaborators */}
                    <p className="text-gray-500 text-sm italic">{safeTranslate('tripDetails.noOneYet', 'No one yet. Invite companions above.')}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default TripDetails;
