// Enhance components/Subscription/PremiumContentGuard.jsx

import React from 'react';
import { useSubscription } from '../../hooks/useSubscription';
import { useMobileSubscription } from '../../hooks/useMobileSubscription';
import { deviceDetection } from '../../utils/deviceDetection';

function PremiumContentGuard({ children, fallback, showSubscribeButton = true }) {
  const { isSubscribed, isLoading } = useSubscription();
  const { isSubscribed: isMobileSubscribed, isLoading: isMobileLoading } = useMobileSubscription();
  
  const isMobile = deviceDetection.isMobileApp();
  const isPremiumUser = isMobile ? isMobileSubscribed : isSubscribed;
  const isLoadingSubscription = isMobile ? isMobileLoading : isLoading;
  
  if (isLoadingSubscription) {
    return (
      <div className="p-4 text-center">
        <div className="animate-pulse h-6 w-24 bg-gray-200 rounded mx-auto"></div>
      </div>
    );
  }
  
  if (isPremiumUser) {
    return children;
  }
  
  if (fallback) {
    return fallback;
  }
  
  return (
    <div className="p-6 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-lg shadow-sm text-center">
      <h3 className="text-lg font-semibold mb-2">Premium Feature</h3>
      <p className="text-gray-600 mb-4">
        This feature is available exclusively to premium subscribers.
      </p>
      
      {showSubscribeButton && (
        <button
          onClick={() => window.location.href = '/subscription/plans'}
          className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
        >
          Upgrade Now
        </button>
      )}
    </div>
  );
}

export default PremiumContentGuard;
