// Create new file src/components/Subscription/MobileCodeActivation.jsx

import React, { useState } from 'react';
import { useMobileSubscription } from '../../hooks/useMobileSubscription';

function MobileCodeActivation() {
  const [code, setCode] = useState('');
  const [isActivating, setIsActivating] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  
  const { activateWithCode } = useMobileSubscription();
  
  const formatCode = (input) => {
    // Remove all non-alphanumeric characters
    const cleaned = input.replace(/[^A-Z0-9]/gi, '').toUpperCase();
    
    // Format with dashes after every 4 characters
    let formatted = '';
    for (let i = 0; i < cleaned.length && i < 12; i++) {
      if (i > 0 && i % 4 === 0) {
        formatted += '-';
      }
      formatted += cleaned[i];
    }
    
    return formatted;
  };
  
  const handleChange = (e) => {
    setCode(formatCode(e.target.value));
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsActivating(true);
    
    try {
      // Remove any dashes from the code before submitting
      const cleanCode = code.replace(/-/g, '');
      
      if (cleanCode.length !== 12) {
        throw new Error('Please enter a valid 12-character access code');
      }
      
      const result = await activateWithCode(code);
      
      if (result.success) {
        setSuccess(true);
      } else {
        throw new Error(result.error || 'Failed to activate subscription');
      }
    } catch (error) {
      console.error('Activation error:', error);
      setError(error.message || 'Failed to activate subscription');
    } finally {
      setIsActivating(false);
    }
  };
  
  if (success) {
    return (
      <div className="p-4 bg-white rounded-lg shadow-sm text-center">
        <svg 
          className="w-12 h-12 text-green-500 mx-auto mb-3" 
          fill="none" 
          stroke="currentColor" 
          viewBox="0 0 24 24" 
          xmlns="http://www.w3.org/2000/svg"
        >
          <path 
            strokeLinecap="round" 
            strokeLinejoin="round" 
            strokeWidth={2} 
            d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" 
          />
        </svg>
        
        <h3 className="text-lg font-medium mb-2">Activation Successful!</h3>
        <p className="text-gray-600 mb-4">
          Your premium subscription has been activated on this device.
        </p>
        
        <button
          onClick={() => window.location.reload()}
          className="px-4 py-2 bg-blue-600 text-white rounded-md"
        >
          Start Using Premium Features
        </button>
      </div>
    );
  }
  
  return (
    <div className="p-4 bg-white rounded-lg shadow-sm">
      <h3 className="text-lg font-medium mb-3">Activate Premium</h3>
      
      <p className="text-gray-600 mb-4">
        Enter your access code from your web subscription purchase to unlock premium features.
      </p>
      
      {error && (
        <div className="mb-4 p-3 bg-red-100 text-red-700 text-sm rounded-md">
          {error}
        </div>
      )}
      
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="accessCode" className="block text-sm font-medium text-gray-700 mb-1">
            Access Code
          </label>
          <input
            id="accessCode"
            type="text"
            value={code}
            onChange={handleChange}
            placeholder="XXXX-XXXX-XXXX"
            className="w-full px-3 py-2 border border-gray-300 rounded-md text-center font-mono"
            maxLength="14" // 12 chars + 2 dashes
            required
          />
        </div>
        
        <button
          type="submit"
          className="w-full py-2 bg-blue-600 text-white rounded-md disabled:bg-blue-300"
          disabled={isActivating}
        >
          {isActivating ? 'Activating...' : 'Activate Premium'}
        </button>
      </form>
    </div>
  );
}

export default MobileCodeActivation;
