// src/App.jsx (update)

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import EnhancedTripPlanner from './components/TravelPlanner/EnhancedTripPlanner';
import EnhancedOfflineIndicator from './components/Offline/EnhancedOfflineIndicator';
import Header from './components/UI/Header';
import CheapFlightsDashboard from './components/CheapFlights/CheapFlightsDashboard';
import { SettingsProvider } from './context/SettingsContext';
import { SubscriptionProvider } from './context/SubscriptionContext';
import { AuthProvider } from './context/AuthContext';
import { I18nProvider } from './utils/i18n';
import AppSettingsWrapper from './components/UI/AppSettingsWrapper';
import UserSettings from './components/Settings/UserSettings';
import MobileNavigation from './components/UI/MobileNavigation';
import { useDeviceDetection } from './utils/deviceDetection';
import LoginForm from './components/Auth/LoginForm';
import RegisterForm from './components/Auth/RegisterForm';
import PrivateRoute from './components/Auth/PrivateRoute';
import PremiumFeaturesNotice from './components/CheapFlights/PremiumFeaturesNotice';
import NotificationSettings from './components/Settings/NotificationSettings';

// Import the subscription components with the correct path
import PlanCards from './components/Accessibility/Subscription/PlanCards';
import Checkout from './components/Accessibility/Subscription/Checkout';
import ManageSubscription from './components/Accessibility/Subscription/ManageSubscription';

// Import the TravelPlanner components
import TripPlanner from './components/TravelPlanner/TripPlanner';
import TripsList from './components/TravelPlanner/TripsList';
import TemplateManager from './components/TripTemplates/TemplateManager';

// App Routes component with state for trip planning
function AppRoutes() {
  const deviceInfo = useDeviceDetection();
  
  // State for trip management
  const [trips, setTrips] = useState([]);
  const [currentTripId, setCurrentTripId] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [view, setView] = useState('dashboard');
  const [tab, setTab] = useState('basic');
  
  // Basic trip information state
  const [destination, setDestination] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [budget, setBudget] = useState('');
  const [budgetCategories, setBudgetCategories] = useState({
    accommodation: 0,
    food: 0,
    transportation: 0,
    activities: 0,
    other: 0
  });
  
  // Trip details state
  const [tripNotes, setTripNotes] = useState([]);
  const [tripTasks, setTripTasks] = useState([]);
  const [transports, setTransports] = useState([]);
  const [photos, setPhotos] = useState([]);
  const [externalServices, setExternalServices] = useState([]);
  
  // Form state
  const [noteTitle, setNoteTitle] = useState('');
  const [noteText, setNoteText] = useState('');
  const [noteCategory, setNoteCategory] = useState('General');
  const [transportType, setTransportType] = useState('');
  const [transportFrom, setTransportFrom] = useState('');
  const [transportTo, setTransportTo] = useState('');
  const [transportPrice, setTransportPrice] = useState('');
  const [photoUrl, setPhotoUrl] = useState('');
  const [photoCaption, setPhotoCaption] = useState('');
  const [serviceType, setServiceType] = useState('');
  const [serviceUrl, setServiceUrl] = useState('');
  const [serviceNote, setServiceNote] = useState('');
  
  // Trip management functions
  const viewTrip = (trip) => {
    // View trip details
    console.log('Viewing trip:', trip);
  };
  
  const editTrip = (trip) => {
    // Set up edit mode with trip data
    setEditMode(true);
    setCurrentTripId(trip.id);
    setDestination(trip.destination);
    setStartDate(trip.startDate);
    setEndDate(trip.endDate);
    setBudget(trip.budget);
    setBudgetCategories(trip.budgetBreakdown || {
      accommodation: 0,
      food: 0,
      transportation: 0,
      activities: 0,
      other: 0
    });
    setTripNotes(trip.notes || []);
    setTripTasks(trip.tasks || []);
    setTransports(trip.transports || []);
    setPhotos(trip.photos || []);
    setExternalServices(trip.externalServices || []);
    setTab('basic');
    setView('planner');
  };
  
  const deleteTrip = (tripId) => {
    // Delete trip from state
    setTrips(trips.filter(trip => trip.id !== tripId));
  };
  
  const compareTrips = () => {
    // Compare selected trips
    console.log('Comparing trips');
  };
  
  const handleUseTemplate = (template) => {
    // Apply template data to a new trip
    console.log('Using template:', template);
  };
  
  return (
    <div className="App">
      {/* ONLY ONE HEADER COMPONENT */}
      <Header />
      
      <Routes>
        {/* Main app routes */}
        <Route path="/" element={<EnhancedTripPlanner />} />
        <Route path="/flights" element={<CheapFlightsDashboard />} />
        
        {/* Travel Planner routes */}
        <Route path="/planner" element={
          <TripPlanner 
            tab={tab}
            setTab={setTab}
            editMode={editMode}
            setEditMode={setEditMode}
            currentTripId={currentTripId}
            setCurrentTripId={setCurrentTripId}
            destination={destination}
            setDestination={setDestination}
            startDate={startDate}
            setStartDate={setStartDate}
            endDate={endDate}
            setEndDate={setEndDate}
            budget={budget}
            setBudget={setBudget}
            budgetCategories={budgetCategories}
            setBudgetCategories={setBudgetCategories}
            tripNotes={tripNotes}
            setTripNotes={setTripNotes}
            transports={transports}
            setTransports={setTransports}
            photos={photos}
            setPhotos={setPhotos}
            externalServices={externalServices}
            setExternalServices={setExternalServices}
            noteTitle={noteTitle}
            setNoteTitle={setNoteTitle}
            noteText={noteText}
            setNoteText={setNoteText}
            noteCategory={noteCategory}
            setNoteCategory={setNoteCategory}
            transportType={transportType}
            setTransportType={setTransportType}
            transportFrom={transportFrom}
            setTransportFrom={setTransportFrom}
            transportTo={transportTo}
            setTransportTo={setTransportTo}
            transportPrice={transportPrice}
            setTransportPrice={setTransportPrice}
            photoUrl={photoUrl}
            setPhotoUrl={setPhotoUrl}
            photoCaption={photoCaption}
            setPhotoCaption={setPhotoCaption}
            serviceType={serviceType}
            setServiceType={setServiceType}
            serviceUrl={serviceUrl}
            setServiceUrl={setServiceUrl}
            serviceNote={serviceNote}
            setServiceNote={setServiceNote}
            trips={trips}
            setTrips={setTrips}
            setView={setView}
            tripTasks={tripTasks}
            setTripTasks={setTripTasks}
          />
        } />
        
        <Route path="/trips" element={
          <TripsList 
            trips={trips}
            viewTrip={viewTrip}
            editTrip={editTrip}
            deleteTrip={deleteTrip}
            compareTrips={compareTrips}
          />
        } />
        
        <Route path="/templates" element={
          <TemplateManager 
            trips={trips} 
            onUseTemplate={handleUseTemplate}
            onClose={() => window.history.back()}
          />
        } />
        
        {/* Auth routes */}
        <Route path="/login" element={<LoginForm />} />
        <Route path="/register" element={<RegisterForm />} />
        
        {/* Protected routes */}
        <Route path="/settings" element={
          <PrivateRoute>
            <UserSettings />
          </PrivateRoute>
        } />
        
        <Route path="/notification-settings" element={
          <PrivateRoute>
            <NotificationSettings />
          </PrivateRoute>
        } />
        
        {/* Subscription routes */}
        <Route path="/subscription/plans" element={
          <PrivateRoute>
            <PlanCards />
          </PrivateRoute>
        } />
        
        <Route path="/subscription/checkout/:planId" element={
          <PrivateRoute>
            <Checkout />
          </PrivateRoute>
        } />
        
        <Route path="/subscription/manage" element={
          <PrivateRoute>
            <ManageSubscription />
          </PrivateRoute>
        } />
      </Routes>
      
      {/* Offline indicator */}
      <EnhancedOfflineIndicator />
      
      {/* Premium features notice for free users */}
      <PremiumFeaturesNotice />
      
      {/* Mobile Navigation for small screens */}
      {deviceInfo.isMobile && <MobileNavigation />}
    </div>
  );
}

function App() {
  return (
    <Router>
      <AuthProvider>
        <SubscriptionProvider>
          <SettingsProvider>
            <I18nProvider>
              <AppSettingsWrapper>
                <AppRoutes />
              </AppSettingsWrapper>
            </I18nProvider>
          </SettingsProvider>
        </SubscriptionProvider>
      </AuthProvider>
    </Router>
  );
}

export default App;
