// src/components/Subscription/PremiumContentGuard.jsx

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSubscription } from '../../hooks/useSubscription';

/**
 * Component that guards premium content
 * Only allows access to children if user has an active subscription
 */
const PremiumContentGuard = ({ children, showPreview = false, previewContent = null }) => {
  const { isSubscribed, isLoading } = useSubscription();
  const navigate = useNavigate();
  const [isShowingDetails, setIsShowingDetails] = useState(false);
  
  // Show loading state while checking subscription status
  if (isLoading) {
    return (
      <div className="flex flex-col items-center justify-center p-8">
        <div className="animate-spin rounded-full h-10 w-10 border-b-2 border-blue-600"></div>
        <p className="mt-4 text-gray-600">Checking subscription status...</p>
      </div>
    );
  }
  
  // If subscribed, render children
  if (isSubscribed) {
    return children;
  }
  
  // Otherwise, show subscription gate
  return (
    <div className="max-w-2xl mx-auto mt-8 p-6">
      {/* Preview content (if enabled) */}
      {showPreview && previewContent && (
        <div className="mb-6">
          {previewContent}
        </div>
      )}
      
      {/* Premium content gate */}
      <div className="bg-gradient-to-r from-blue-50 to-indigo-50 p-6 rounded-lg shadow-sm border border-blue-100">
        <div className="flex items-center justify-center mb-4">
          <svg className="w-12 h-12 text-indigo-500" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
            <path fillRule="evenodd" d="M5 9V7a5 5 0 0110 0v2a2 2 0 012 2v5a2 2 0 01-2 2H5a2 2 0 01-2-2v-5a2 2 0 012-2zm8-2v2H7V7a3 3 0 016 0z" clipRule="evenodd"></path>
          </svg>
        </div>
        
        <h2 className="text-2xl font-bold text-center text-gray-800 mb-2">
          Premium Feature
        </h2>
        
        <p className="text-center text-gray-600 mb-6">
          This feature requires a premium subscription. 
          {!isShowingDetails && (
            <button
              onClick={() => setIsShowingDetails(true)}
              className="ml-1 text-indigo-600 hover:text-indigo-800 hover:underline focus:outline-none focus:underline"
            >
              View details
            </button>
          )}
        </p>
        
        {isShowingDetails && (
          <div className="bg-white p-4 rounded-md mb-6">
            <h3 className="font-semibold text-gray-800 mb-2">Premium Benefits:</h3>
            <ul className="list-disc pl-5 mb-4 text-gray-700 space-y-1">
              <li>Unlimited flight deal alerts</li>
              <li>Real-time price drop notifications</li>
              <li>Advanced trip planning tools</li>
              <li>Personalized travel recommendations</li>
              <li>Ad-free experience</li>
            </ul>
            <p className="text-sm text-gray-600">
              Starting at just $4.99 per month. Cancel anytime.
            </p>
          </div>
        )}
        
        <div className="flex flex-col sm:flex-row space-y-3 sm:space-y-0 sm:space-x-3 justify-center">
          <button
            onClick={() => navigate('/subscription/plans')}
            className="bg-indigo-600 text-white px-6 py-2 rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
          >
            Subscribe Now
          </button>
          
          <button
            onClick={() => navigate('/')}
            className="border border-gray-300 bg-white text-gray-700 px-6 py-2 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
          >
            Return to Dashboard
          </button>
        </div>
      </div>
    </div>
  );
};

export default PremiumContentGuard;
