// src/data/dataProvider.js
const inMemoryProvider = require('./inMemoryProvider');
const mongoProvider = require('./mongoProvider');

// Determine which provider to use - allows for easy switching
// Use environment variable to control which data provider to use
const useMongoDb = process.env.USE_MONGODB === 'true';

// Export the appropriate data provider
const dataProvider = useMongoDb ? mongoProvider : inMemoryProvider;

console.log(`Using ${useMongoDb ? 'MongoDB' : 'in-memory'} data provider`);

module.exports = dataProvider;
