import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import Offers from './Offers';
import AdminOffers from '../Admin/AdminOffers';
import Login from '../Auth/Login';
import AccessibleButton from '../Accessibility/AccessibleButton';

const OffersMain = () => {
  const { currentUser, isAdmin, logout } = useAuth();
  const [showAdminView, setShowAdminView] = useState(false);
  const [showLoginForm, setShowLoginForm] = useState(false);
  
  // Check if user is admin and update showAdminView if needed
  useEffect(() => {
    if (isAdmin() && currentUser) {
      setShowAdminView(true);
    } else {
      setShowAdminView(false);
    }
  }, [isAdmin, currentUser]);
  
  // Handle successful login
  const handleLoginSuccess = () => {
    setShowLoginForm(false);
    // If user is admin, show admin view
    if (isAdmin()) {
      setShowAdminView(true);
    }
  };

  // Handle revealing admin access
  useEffect(() => {
    // Track key presses for the admin login trigger
    let keys = [];
    const adminKeyCombo = 'admin';
    
    const handleKeyPress = (e) => {
      // Add the key to the array
      keys.push(e.key.toLowerCase());
      
      // Only keep the last letters needed for combo
      if (keys.length > adminKeyCombo.length) {
        keys.shift();
      }
      
      // Check if the keys match the admin combo
      if (keys.join('') === adminKeyCombo) {
        // Show the admin login trigger
        const adminTrigger = document.getElementById('admin-login-trigger');
        if (adminTrigger) {
          adminTrigger.classList.add('show');
        }
        
        // Reset keys after successful combo
        keys = [];
      }
    };
    
    window.addEventListener('keydown', handleKeyPress);
    
    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, []);
  
  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      {/* Admin controls */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-800">Travel Offers</h1>
        
        <div className="flex items-center space-x-4">
          {currentUser ? (
            <>
              <span className="text-gray-600">
                Welcome, <span className="font-medium">{currentUser.username}</span>
                {isAdmin() && <span className="ml-1 text-blue-600">(Admin)</span>}
              </span>
              
              {isAdmin() && (
                <AccessibleButton
                  variant={showAdminView ? "secondary" : "primary"}
                  onClick={() => setShowAdminView(!showAdminView)}
                  size="small"
                >
                  {showAdminView ? "View Customer View" : "Manage Offers"}
                </AccessibleButton>
              )}
              
              <AccessibleButton
                variant="outline"
                onClick={logout}
                size="small"
              >
                Logout
              </AccessibleButton>
            </>
          ) : (
            // This admin login button is hidden by CSS
            <div className="admin-login-btn">
              <AccessibleButton
                variant="primary"
                onClick={() => setShowLoginForm(true)}
                size="small"
              >
                Admin Login
              </AccessibleButton>
            </div>
          )}
        </div>
      </div>
      
      {/* Login form */}
      {showLoginForm && (
        <div className="mb-8">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-semibold text-gray-800">Admin Login</h2>
            <button 
              onClick={() => setShowLoginForm(false)}
              className="text-gray-600 hover:text-gray-800"
            >
              ✕ Close
            </button>
          </div>
          <Login onLoginSuccess={handleLoginSuccess} />
        </div>
      )}
      
      {/* Main content */}
      {isAdmin() && showAdminView ? (
        <AdminOffers />
      ) : (
        <Offers />
      )}

      {/* Add a hidden admin login trigger footer */}
      <div className="admin-access-panel">
        <div className="text-center mt-16 pt-8 border-t border-gray-200">
          <p className="text-xs text-gray-400">© 2025 TravelEase. All rights reserved.</p>
          {/* This span will be hidden until the admin key combo is typed */}
          <span 
            id="admin-login-trigger" 
            className="hidden cursor-pointer mt-2 text-gray-400 hover:text-gray-600"
            onClick={() => setShowLoginForm(true)}
          >
            Admin Access
          </span>
        </div>
      </div>
    </div>
  );
};

export default OffersMain;
