import React from 'react';
import { useI18n } from '../../utils/i18n';

function MobileOptimizedTripDetails({ trip, onEdit, onViewDetails }) {
  const { t } = useI18n();
  
  // Add null check for trip - return a loading state if trip is undefined
  if (!trip) {
    return (
      <div className="p-4 bg-gray-100 rounded-lg text-center">
        <p>{t('tripDetails.loading', 'Loading trip details...')}</p>
      </div>
    );
  }

  // Use optional chaining to safely access nested properties
  const destinationName = trip?.destination?.name || t('tripDetails.noDestination', 'No destination');
  const startDate = trip?.startDate ? new Date(trip.startDate) : null;
  const endDate = trip?.endDate ? new Date(trip.endDate) : null;
  
  // Format dates safely
  const formatDate = (date) => {
    if (!date) return '';
    return date.toLocaleDateString(undefined, { month: 'short', day: 'numeric', year: 'numeric' });
  };
  
  // Calculate trip duration safely
  const calculateDuration = () => {
    if (!startDate || !endDate) return '';
    
    const diffTime = Math.abs(endDate - startDate);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    return diffDays === 1 
      ? t('tripDetails.day', '1 day') 
      : t('tripDetails.days', `${diffDays} days`);
  };
  
  return (
    <div className="bg-white rounded-lg shadow p-4">
      <div className="flex justify-between items-start mb-4">
        <div>
          <h2 className="text-xl font-bold">{trip?.name || t('tripDetails.untitled', 'Untitled Trip')}</h2>
          <p className="text-gray-600">
            {destinationName}
            {(startDate && endDate) && (
              <span className="ml-2">•</span>
            )}
            {startDate && (
              <span className="ml-2">
                {formatDate(startDate)}
                {endDate && endDate > startDate && ' - '}
                {endDate && endDate > startDate && formatDate(endDate)}
              </span>
            )}
            {(startDate && endDate) && (
              <span className="ml-2">({calculateDuration()})</span>
            )}
          </p>
        </div>
        
        <div className="flex space-x-2">
          {onEdit && (
            <button
              onClick={() => onEdit(trip)}
              className="p-2 text-blue-600 hover:bg-blue-50 rounded-full"
              aria-label={t('tripDetails.edit', 'Edit Trip')}
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15.232 5.232l3.536 3.536m-2.036-5.036a2.5 2.5 0 113.536 3.536L6.5 21.036H3v-3.572L16.732 3.732z"></path>
              </svg>
            </button>
          )}
          
          {onViewDetails && (
            <button
              onClick={() => onViewDetails(trip)}
              className="p-2 text-blue-600 hover:bg-blue-50 rounded-full"
              aria-label={t('tripDetails.viewDetails', 'View Details')}
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"></path>
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z"></path>
              </svg>
            </button>
          )}
        </div>
      </div>
      
      {/* Trip status or progress */}
      {trip?.status && (
        <div className="mb-3">
          <div className="bg-blue-50 text-blue-700 px-3 py-1 rounded-full inline-block text-sm">
            {trip.status}
          </div>
        </div>
      )}
      
      {/* Trip activities preview */}
      {trip?.activities && trip.activities.length > 0 && (
        <div className="mt-4">
          <h3 className="text-sm font-medium text-gray-700 mb-2">
            {t('tripDetails.activities', 'Activities')}
          </h3>
          <ul className="space-y-1">
            {trip.activities.slice(0, 3).map((activity, index) => (
              <li key={index} className="text-sm flex items-center">
                <span className="w-2 h-2 bg-blue-500 rounded-full mr-2"></span>
                {activity.name || t('tripDetails.unnamedActivity', 'Unnamed Activity')}
              </li>
            ))}
            {trip.activities.length > 3 && (
              <li className="text-sm text-gray-500">
                {t('tripDetails.moreActivities', '+ {{count}} more', { count: trip.activities.length - 3 })}
              </li>
            )}
          </ul>
        </div>
      )}
    </div>
  );
}

export default MobileOptimizedTripDetails;
