import React, { useState } from 'react';
import { calculateDuration } from '../../utils/helpers';
import { formatCurrency } from '../../utils/settingsUtils';
import { useI18n } from '../../utils/i18n';
import MobileOptimizedTripDetails from './MobileOptimizedTripDetails';

function MobileOptimizedDashboard({ trips, viewTrip, onNewTrip }) {
  const [selectedTrip, setSelectedTrip] = useState(null);
  const [viewingTripDetails, setViewingTripDetails] = useState(false);
  const { t } = useI18n();
  
  // Format date for mobile display
  const formatMobileDate = (dateString) => {
    if (!dateString) return '';
    
    const date = new Date(dateString);
    return date.toLocaleDateString(undefined, { month: 'short', day: 'numeric' });
  };
  
  const handleTripClick = (trip) => {
    if (!trip) return;
    
    setSelectedTrip(trip);
    setViewingTripDetails(true);
  };
  
  const handleCloseTripDetails = () => {
    setViewingTripDetails(false);
    setSelectedTrip(null);
  };
  
  const handleEditTrip = (trip) => {
    if (!trip) return;
    
    viewTrip(trip);
  };
  
  // Ensure a safe render of trip details by checking selectedTrip explicitly
  if (viewingTripDetails && selectedTrip) {
    return (
      <MobileOptimizedTripDetails
        trip={selectedTrip}
        editTrip={handleEditTrip}
        closeTrip={handleCloseTripDetails}
        shareEmail=""
        setShareEmail={() => {}}
        userSettings={{}}
      />
    );
  }
  
  return (
    <div className="mobile-dashboard">
      <div className="p-4">
        <h1 className="text-2xl font-bold text-gray-800 mb-4">{t('dashboard.title')}</h1>
        
        {/* New Trip Button */}
        <button
          onClick={onNewTrip}
          className="w-full bg-blue-600 text-white py-3 rounded-lg mb-6 flex items-center justify-center"
        >
          <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 4v16m8-8H4"></path>
          </svg>
          {t('actions.newTrip')}
        </button>
        
        {/* Upcoming Trips */}
        <h2 className="text-xl font-semibold text-gray-700 mb-3">{t('dashboard.upcomingTrips')}</h2>
        
        {trips.length === 0 ? (
          <div className="bg-gray-100 p-6 rounded-lg text-center">
            <p className="text-gray-500 mb-4">{t('dashboard.noTrips')}</p>
            <button
              onClick={onNewTrip}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg"
            >
              {t('actions.createFirstTrip')}
            </button>
          </div>
        ) : (
          <div className="space-y-4">
            {trips.map(trip => {
              // Safely access trip properties
              const destination = trip?.destination || 'Unknown';
              const startDate = trip?.startDate || '';
              const endDate = trip?.endDate || '';
              const budget = trip?.budget || 0;
              
              return (
                <div 
                  key={trip.id} 
                  className="bg-white p-4 rounded-lg shadow-sm border border-gray-200"
                  onClick={() => handleTripClick(trip)}
                >
                  <h3 className="text-lg font-semibold text-gray-800">{destination}</h3>
                  <div className="flex justify-between items-center mt-1">
                    <div className="text-gray-600">
                      <span>{formatMobileDate(startDate)} - {formatMobileDate(endDate)}</span>
                      <span className="ml-1 text-xs">({calculateDuration(startDate, endDate)} days)</span>
                    </div>
                    <div className="text-blue-600 font-medium">
                      {formatCurrency(budget)}
                    </div>
                  </div>
                  
                  {/* Quick Stats */}
                  <div className="flex mt-3 pt-2 border-t border-gray-100">
                    <div className="flex-1 text-center">
                      <p className="text-xs text-gray-500">{t('labels.tasks')}</p>
                      <p className="font-semibold">{trip.tasks?.length || 0}</p>
                    </div>
                    <div className="flex-1 text-center border-l border-r border-gray-100">
                      <p className="text-xs text-gray-500">{t('labels.photos')}</p>
                      <p className="font-semibold">{trip.photos?.length || 0}</p>
                    </div>
                    <div className="flex-1 text-center">
                      <p className="text-xs text-gray-500">{t('labels.notes')}</p>
                      <p className="font-semibold">{trip.notes?.length || 0}</p>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}

export default MobileOptimizedDashboard;
