import React, { useState } from 'react';
import './App.css';
import EnhancedTripPlanner from './components/TravelPlanner/EnhancedTripPlanner';
import EnhancedOfflineIndicator from './components/Offline/EnhancedOfflineIndicator';
import Header from './components/UI/Header';
// Import the CheapFlightsDashboard component
import CheapFlightsDashboard from './components/CheapFlights/CheapFlightsDashboard';
import { SettingsProvider } from './context/SettingsContext';
import { I18nProvider } from './utils/i18n';
import AppSettingsWrapper from './components/UI/AppSettingsWrapper';
import UserSettings from './components/Settings/UserSettings';
import MobileNavigation from './components/UI/MobileNavigation';
import { useDeviceDetection } from './utils/deviceDetection';

function App() {
  // Add the state variable for the current view
  const [view, setView] = useState('dashboard');
  const [showSettings, setShowSettings] = useState(false);
  const deviceInfo = useDeviceDetection();

  return (
    <SettingsProvider>
      <I18nProvider>
        <AppSettingsWrapper>
          <div className="App">
            {/* Pass the view state and setView function to Header */}
            <Header 
              view={view} 
              setView={setView} 
              onNewTrip={() => setView('planner')}
              onOpenSettings={() => setShowSettings(true)}
              onOpenTemplates={() => {/* Handle templates */}}
            />
            
            {/* EnhancedTripPlanner handles multiple views internally */}
            <EnhancedTripPlanner 
              showSettings={showSettings}
              onOpenSettings={() => setShowSettings(true)}
              onCloseSettings={() => setShowSettings(false)}
              showHeader={false}
              view={view}
              setView={setView}
            />
            
            {/* Show CheapFlightsDashboard when view is 'cheapFlights' */}
            {view === 'cheapFlights' && (
              <CheapFlightsDashboard />
            )}
            
            {/* Settings Modal */}
            {showSettings && (
              <UserSettings onClose={() => setShowSettings(false)} />
            )}
            
            {/* Offline Indicator */}
            <EnhancedOfflineIndicator />
            
            {/* Mobile Navigation for small screens */}
            {deviceInfo.isMobile && (
              <MobileNavigation 
                view={view}
                setView={setView}
                onNewTrip={() => setView('planner')}
                onOpenSettings={() => setShowSettings(true)}
                onOpenTemplates={() => {/* Handle templates */}}
              />
            )}
          </div>
        </AppSettingsWrapper>
      </I18nProvider>
    </SettingsProvider>
  );
}

export default App;
