// Modified App.jsx with improved navigation and trip handling
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import './App.css';
import EnhancedTripPlanner from './components/TravelPlanner/EnhancedTripPlanner';
import EnhancedOfflineIndicator from './components/Offline/EnhancedOfflineIndicator';
import Header from './components/UI/Header';
import CheapFlightsDashboard from './components/CheapFlights/CheapFlightsDashboard';
import { SettingsProvider } from './context/SettingsContext';
import { SubscriptionProvider } from './context/SubscriptionContext';
import { AuthProvider } from './context/AuthContext';
import { I18nProvider } from './utils/i18n';
import AppSettingsWrapper from './components/UI/AppSettingsWrapper';
import UserSettings from './components/Settings/UserSettings';
import MobileNavigation from './components/UI/MobileNavigation';
import { useDeviceDetection } from './utils/deviceDetection';
import LoginForm from './components/Auth/LoginForm';
import RegisterForm from './components/Auth/RegisterForm';
import PrivateRoute from './components/Auth/PrivateRoute';
import PremiumFeaturesNotice from './components/CheapFlights/PremiumFeaturesNotice';
import NotificationSettings from './components/Settings/NotificationSettings';

// Import the subscription components
import PlanCards from './components/Accessibility/Subscription/PlanCards';
import Checkout from './components/Accessibility/Subscription/Checkout';
import ManageSubscription from './components/Accessibility/Subscription/ManageSubscription';

// Import the TravelPlanner components
import TripPlanner from './components/TravelPlanner/TripPlanner';
import TripsList from './components/TravelPlanner/TripsList';
import TemplateManager from './components/TripTemplates/TemplateManager';

// App Routes component with state for trip planning
function AppRoutes() {
  const deviceInfo = useDeviceDetection();
  const [showSettings, setShowSettings] = useState(false);
  
  // State for managing views
  const [view, setView] = useState('dashboard');
  
  // Trip data state (moved to App level for better sharing)
  const [trips, setTrips] = useState([]);
  const [selectedTrip, setSelectedTrip] = useState(null);
  
  // Load trips from localStorage on component mount
  useEffect(() => {
    const savedTrips = localStorage.getItem('travelPlannerTrips');
    if (savedTrips) {
      setTrips(JSON.parse(savedTrips));
    }
  }, []);

  // Save trips to localStorage when trips state changes
  useEffect(() => {
    localStorage.setItem('travelPlannerTrips', JSON.stringify(trips));
  }, [trips]);
  
  // Handle settings functions
  const handleOpenSettings = () => {
    setShowSettings(true);
  };
  
  const handleCloseSettings = () => {
    setShowSettings(false);
  };
  
  // Trip action handlers
  const handleNewTrip = () => {
    setView('planner');
  };
  
  const handleViewTrip = (trip) => {
    setSelectedTrip(trip);
    setView('tripDetails');
  };
  
  const handleEditTrip = (trip) => {
    setSelectedTrip(trip);
    setView('planner');
  };
  
  const handleDeleteTrip = (tripId) => {
    if (window.confirm('Are you sure you want to delete this trip?')) {
      setTrips(trips.filter(trip => trip.id !== tripId));
      if (selectedTrip && selectedTrip.id === tripId) {
        setSelectedTrip(null);
      }
    }
  };
  
  const handleCompareTrips = () => {
    // This would be implemented in EnhancedTripPlanner
    setView('compare');
  };
  
  return (
    <div className="App">
      {/* Single header instance */}
      <Header 
        view={view} 
        setView={setView} 
        onNewTrip={handleNewTrip}
        onOpenSettings={handleOpenSettings}
      />
      
      <Routes>
        {/* Main app routes */}
        <Route path="/" element={
          <EnhancedTripPlanner 
            showHeader={false}
            view={view}
            setView={setView}
            showSettings={showSettings}
            onOpenSettings={handleOpenSettings}
            onCloseSettings={handleCloseSettings}
            trips={trips}
            setTrips={setTrips}
            selectedTrip={selectedTrip}
            setSelectedTrip={setSelectedTrip}
            onViewTrip={handleViewTrip}
            onEditTrip={handleEditTrip}
            onDeleteTrip={handleDeleteTrip}
            onNewTrip={handleNewTrip}
            onCompareTrips={handleCompareTrips}
          />
        } />
        
        <Route path="/flights" element={<CheapFlightsDashboard />} />
        
        {/* Travel Planner routes */}
        <Route path="/planner" element={
          <EnhancedTripPlanner 
            showHeader={false}
            view="planner"
            setView={setView}
            showSettings={showSettings}
            onOpenSettings={handleOpenSettings}
            onCloseSettings={handleCloseSettings}
            trips={trips}
            setTrips={setTrips}
            selectedTrip={selectedTrip}
            setSelectedTrip={setSelectedTrip}
            onViewTrip={handleViewTrip}
            onEditTrip={handleEditTrip}
            onDeleteTrip={handleDeleteTrip}
            onNewTrip={handleNewTrip}
            onCompareTrips={handleCompareTrips}
          />
        } />
        
        <Route path="/trips" element={
          <EnhancedTripPlanner 
            showHeader={false}
            view="trips"
            setView={setView}
            showSettings={showSettings}
            onOpenSettings={handleOpenSettings}
            onCloseSettings={handleCloseSettings}
            trips={trips}
            setTrips={setTrips}
            selectedTrip={selectedTrip}
            setSelectedTrip={setSelectedTrip}
            onViewTrip={handleViewTrip}
            onEditTrip={handleEditTrip}
            onDeleteTrip={handleDeleteTrip}
            onNewTrip={handleNewTrip}
            onCompareTrips={handleCompareTrips}
          />
        } />
        
        <Route path="/templates" element={
          <TemplateManager 
            trips={trips} 
            onUseTemplate={(template) => {
              // Handle template usage
              handleNewTrip();
            }}
            onClose={() => window.history.back()}
          />
        } />
        
        {/* Auth routes */}
        <Route path="/login" element={<LoginForm redirectTo="/" />} />
        <Route path="/register" element={<RegisterForm redirectTo="/" />} />
        
        {/* Protected routes */}
        <Route path="/settings" element={
          <PrivateRoute>
            <UserSettings />
          </PrivateRoute>
        } />
        
        <Route path="/notification-settings" element={
          <PrivateRoute>
            <NotificationSettings />
          </PrivateRoute>
        } />
        
        {/* Subscription routes */}
        <Route path="/subscription/plans" element={
          <PrivateRoute>
            <PlanCards onSelectPlan={(plan) => navigate(`/subscription/checkout/${plan.id}`)} />
          </PrivateRoute>
        } />
        
        <Route path="/subscription/checkout/:planId" element={
          <PrivateRoute>
            <Checkout onBack={() => navigate('/subscription/plans')} />
          </PrivateRoute>
        } />
        
        <Route path="/subscription/manage" element={
          <PrivateRoute>
            <ManageSubscription />
          </PrivateRoute>
        } />
      </Routes>
      
      {/* Offline indicator */}
      <EnhancedOfflineIndicator />
      
      {/* Premium features notice for free users */}
      <PremiumFeaturesNotice />
      
      {/* Mobile Navigation for small screens */}
      {deviceInfo.isMobile && (
        <MobileNavigation 
          view={view}
          setView={setView}
          onNewTrip={handleNewTrip}
        />
      )}
    </div>
  );
}

function App() {
  return (
    <Router>
      <AuthProvider>
        <SubscriptionProvider>
          <SettingsProvider>
            <I18nProvider>
              <AppSettingsWrapper>
                <AppRoutes />
              </AppSettingsWrapper>
            </I18nProvider>
          </SettingsProvider>
        </SubscriptionProvider>
      </AuthProvider>
    </Router>
  );
}

export default App;
