// Minimal fix for TripsList.jsx - focusing only on button handlers
import React from 'react';
import { calculateDuration } from '../../utils/helpers';
import { useI18n } from '../../utils/i18n';

function TripsList({ trips, viewTrip, editTrip, deleteTrip, compareTrips }) {
  const { t } = useI18n();
  
  // Add debugging to button handlers
  const handleViewTrip = (trip) => {
    console.log("View Details clicked for:", trip.destination);
    if (typeof viewTrip === 'function') {
      viewTrip(trip);
    } else {
      console.error("viewTrip function is not defined or not passed correctly");
      alert("View functionality is not available. Check console for details.");
    }
  };
  
  const handleEditTrip = (trip) => {
    console.log("Edit Trip clicked for:", trip.destination);
    if (typeof editTrip === 'function') {
      editTrip(trip);
    } else {
      console.error("editTrip function is not defined or not passed correctly");
      alert("Edit functionality is not available. Check console for details.");
    }
  };
  
  const handleDeleteTrip = (tripId) => {
    console.log("Delete clicked for trip ID:", tripId);
    if (typeof deleteTrip === 'function') {
      if (confirm("Are you sure you want to delete this trip?")) {
        deleteTrip(tripId);
      }
    } else {
      console.error("deleteTrip function is not defined or not passed correctly");
      alert("Delete functionality is not available. Check console for details.");
    }
  };
  
  return (
    <div className="max-w-6xl mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-semibold text-gray-800">{t('trips.myTrips', 'My Trips')}</h2>
        {trips.length >= 2 && (
          <button
            onClick={compareTrips}
            className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700 flex items-center"
          >
            <span className="mr-2">⚖️</span> {t('trips.compareTrips', 'Compare Trips')}
          </button>
        )}
      </div>
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {trips.map(trip => (
          <div key={trip.id} className="bg-white rounded-lg shadow overflow-hidden hover-card">
            <div className="bg-blue-600 text-white p-3">
              <h3 className="text-xl font-bold">{trip.destination}</h3>
            </div>
            <div className="p-4">
              <p className="mb-1"><strong>{t('trips.dates', 'Dates')}:</strong> {trip.startDate} {t('trips.to', 'to')} {trip.endDate}</p>
              <p><strong>{t('trips.duration', 'Duration')}:</strong> {calculateDuration(trip.startDate, trip.endDate)} {t('trips.days', 'days')}</p>
              {trip.budget && <p><strong>{t('trips.budget', 'Budget')}:</strong> ${trip.budget}</p>}
              
              {/* Keep your existing content, just modify the button handlers */}
              
              <div className="mt-4 flex flex-wrap gap-2">
                <button
                  onClick={() => handleViewTrip(trip)}
                  className="bg-blue-500 text-white px-3 py-1 rounded text-sm hover:bg-blue-600"
                >
                  {t('trips.viewDetails', 'View Details')}
                </button>
                <button
                  onClick={() => handleEditTrip(trip)}
                  className="bg-yellow-500 text-white px-3 py-1 rounded text-sm hover:bg-yellow-600"
                >
                  {t('trips.editTrip', 'Edit Trip')}
                </button>
                <button
                  onClick={() => handleDeleteTrip(trip.id)}
                  className="bg-red-500 text-white px-3 py-1 rounded text-sm hover:bg-red-600"
                >
                  {t('trips.delete', 'Delete')}
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TripsList;
