import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import AirportAutocomplete from '../FlightSearch/AirportAutocomplete';

function LocationSelector({ onLocationChange }) {
  const [origin, setOrigin] = useState(null);
  const [destination, setDestination] = useState(null);
  const [error, setError] = useState('');
  // Remove the unused setLoading variable
  const [loading] = useState(false);

  useEffect(() => {
    // Only trigger the change when both origin and destination are selected
    if (origin && destination) {
      onLocationChange({ origin, destination });
    }
  }, [origin, destination, onLocationChange]);

  const handleSwapLocations = () => {
    // Swap origin and destination
    const temp = origin;
    setOrigin(destination);
    setDestination(temp);
  };

  const validateAndSetOrigin = (airport) => {
    if (airport && destination && airport.code === destination.code) {
      setError('Origin and destination cannot be the same');
    } else {
      setError('');
      setOrigin(airport);
    }
  };

  const validateAndSetDestination = (airport) => {
    if (airport && origin && airport.code === origin.code) {
      setError('Origin and destination cannot be the same');
    } else {
      setError('');
      setDestination(airport);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-4 mb-6">
      <div className="flex flex-col md:flex-row items-center gap-4 relative">
        {/* Origin input */}
        <div className="w-full md:w-2/5">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            From
          </label>
          <AirportAutocomplete
            placeholder="Departure city or airport"
            onSelect={validateAndSetOrigin}
            selectedAirport={origin}
          />
        </div>

        {/* Swap button */}
        <motion.button
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          className="absolute left-1/2 top-1/2 transform -translate-x-1/2 -translate-y-1/2 md:static md:transform-none bg-blue-500 text-white p-2 rounded-full shadow-md hidden md:block"
          onClick={handleSwapLocations}
          aria-label="Swap origin and destination"
        >
          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 7h12m0 0l-4-4m4 4l-4 4m0 6H4m0 0l4 4m-4-4l4-4"></path>
          </svg>
        </motion.button>

        {/* Destination input */}
        <div className="w-full md:w-2/5">
          <label className="block text-gray-700 text-sm font-bold mb-2">
            To
          </label>
          <AirportAutocomplete
            placeholder="Arrival city or airport"
            onSelect={validateAndSetDestination}
            selectedAirport={destination}
          />
        </div>
      </div>

      {/* Error message */}
      {error && (
        <div className="mt-2 text-red-500 text-sm">
          {error}
        </div>
      )}
    </div>
  );
}

export default LocationSelector;
