// src/components/Dashboard/MobileOptimizedDashboard.jsx

import React, { useState } from 'react';
import { calculateDuration, exportTripToPDF } from '../../utils/helpers';
import { useAppSettings } from '../../utils/useAppSettings';

function MobileOptimizedTripDetails({ trip, editTrip, closeTrip, shareEmail, setShareEmail }) {
  // Always call hooks at the top level, never conditionally
  const [activeTab, setActiveTab] = useState('overview');
  const [showActionsMenu, setShowActionsMenu] = useState(false);
  const { currency, date: formatDate } = useAppSettings();
  
  // Format date for mobile display
  const formatMobileDate = (dateString) => {
    if (!dateString) return '';
    
    const date = new Date(dateString);
    return date.toLocaleDateString(undefined, { month: 'short', day: 'numeric', year: 'numeric' });
  };
  
  // Handle PDF export
  const handleExportPDF = () => {
    if (!trip) return;
    exportTripToPDF(trip);
    setShowActionsMenu(false);
  };
  
  // Handle email sharing
  const handleEmailShare = () => {
    if (!shareEmail || !trip) {
      alert('Please enter an email address');
      return;
    }
    
    // In a real implementation, this would send an email
    alert(`Trip details for ${trip.destination} sent to ${shareEmail}`);
    setShareEmail('');
    setShowActionsMenu(false);
  };
  
  // Now we can have our conditional return after all hooks are called
  if (!trip) {
    return (
      <div className="px-4 max-w-md mx-auto py-8 text-center">
        <h2 className="text-xl font-bold text-gray-800 mb-3">Trip Not Found</h2>
        <p className="text-gray-600 mb-4">The trip information is not available.</p>
        <button
          onClick={closeTrip}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg"
        >
          Go Back
        </button>
      </div>
    );
  }
  
  return (
    <div className="px-4 max-w-md mx-auto pb-20">
      {/* Header */}
      <div className="sticky top-0 z-10 bg-white pb-3 pt-2">
        <div className="flex justify-between items-center mb-2">
          <button
            onClick={closeTrip}
            className="text-gray-600"
            aria-label="Go back"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18"></path>
            </svg>
          </button>
          
          <div className="relative">
            <button
              onClick={() => setShowActionsMenu(!showActionsMenu)}
              className="text-gray-600"
              aria-label="More actions"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z"></path>
              </svg>
            </button>
            
            {/* Actions Menu */}
            {showActionsMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg z-10 border border-gray-200">
                <div className="py-1">
                  <button
                    onClick={() => {
                      editTrip(trip);
                      setShowActionsMenu(false);
                    }}
                    className="w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100"
                  >
                    Edit Trip
                  </button>
                  <button
                    onClick={handleExportPDF}
                    className="w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100"
                  >
                    Export as PDF
                  </button>
                  <button
                    onClick={() => {
                      if (navigator.share) {
                        navigator.share({
                          title: `Trip to ${trip?.destination || 'Unknown Destination'}`,
                          text: `Check out my trip to ${trip?.destination || 'Unknown Destination'} from ${formatDate(trip?.startDate || new Date())} to ${formatDate(trip?.endDate || new Date())}!`,
                          url: window.location.href
                        });
                      } else {
                        alert('Web Share API not supported on this browser');
                      }
                      setShowActionsMenu(false);
                    }}
                    className="w-full text-left px-4 py-2 text-gray-700 hover:bg-gray-100"
                  >
                    Share Trip
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
        
        <h2 className="text-2xl font-bold text-gray-800">{trip?.destination || 'No Destination'}</h2>
        <p className="text-gray-600">
          {formatDate(trip?.startDate)} - {formatDate(trip?.endDate)}
          <span className="ml-2 text-sm">({calculateDuration(trip?.startDate, trip?.endDate)} days)</span>
        </p>
        
        {/* Tab Navigation */}
        <div className="flex overflow-x-auto hide-scrollbar border-b border-gray-200 mt-4 -mx-4 px-4">
          <button
            className={`mr-4 py-2 font-medium whitespace-nowrap ${
              activeTab === 'overview' 
                ? 'border-b-2 border-blue-500 text-blue-600' 
                : 'text-gray-500'
            }`}
            onClick={() => setActiveTab('overview')}
          >
            Overview
          </button>
          <button
            className={`mr-4 py-2 font-medium whitespace-nowrap ${
              activeTab === 'itinerary' 
                ? 'border-b-2 border-blue-500 text-blue-600' 
                : 'text-gray-500'
            }`}
            onClick={() => setActiveTab('itinerary')}
          >
            Itinerary
          </button>
          <button
            className={`mr-4 py-2 font-medium whitespace-nowrap ${
              activeTab === 'tasks' 
                ? 'border-b-2 border-blue-500 text-blue-600' 
                : 'text-gray-500'
            }`}
            onClick={() => setActiveTab('tasks')}
          >
            Tasks
          </button>
          <button
            className={`mr-4 py-2 font-medium whitespace-nowrap ${
              activeTab === 'budget' 
                ? 'border-b-2 border-blue-500 text-blue-600' 
                : 'text-gray-500'
            }`}
            onClick={() => setActiveTab('budget')}
          >
            Budget
          </button>
          <button
            className={`mr-4 py-2 font-medium whitespace-nowrap ${
              activeTab === 'notes' 
                ? 'border-b-2 border-blue-500 text-blue-600' 
                : 'text-gray-500'
            }`}
            onClick={() => setActiveTab('notes')}
          >
            Notes
          </button>
        </div>
      </div>
      
      {/* Rest of your component... */}
      {/* Keep all the existing tab content code, but ensure all data access uses optional chaining */}
      
      {/* Bottom Action Bar */}
      <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 p-4 flex justify-between z-20">
        <button
          onClick={() => editTrip(trip)}
          className="w-1/2 bg-blue-600 text-white py-2 rounded-l-lg flex items-center justify-center"
        >
          <svg className="w-5 h-5 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z"></path>
          </svg>
          Edit Trip
        </button>
        <button
          onClick={handleExportPDF}
          className="w-1/2 bg-gray-600 text-white py-2 rounded-r-lg flex items-center justify-center"
        >
          <svg className="w-5 h-5 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-4l-4 4m0 0l-4-4m4 4V4"></path>
          </svg>
          Export PDF
        </button>
      </div>
    </div>
  );
}

export default MobileOptimizedTripDetails;
